    <script>
        function followAuthor(authorId) {
            showCalimaticLoader();
            $(".error_msg").html('');
            var data = new FormData($('#add_form')[0]);

            $.ajax({
                headers: {
                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                },
                method: "POST",
                url: "{{ url('follow-author') }}",
                data: {
                    author_id: authorId
                },
                success: function(data, textStatus, jqXHR) {
                    if (data.status == 1) {

                        $('#author' + authorId).text('Following');
                    }

                }
            }).done(function() {
                $("#success_msg").html("Data Save Successfully");
                //  window.location.href = "{{ url('admin/users') }}";
                // location.reload();
            }).fail(function(data, textStatus, jqXHR) {
                var json_data = JSON.parse(data.responseText);
                $.each(json_data.errors, function(key, value) {
                    $("#" + key).after("<span class='error_msg' style='color: red;font-weigh: 600'>" +
                        value + "</span>");
                });
            });
            HideCalimaticLoader();
        }

        function UnfollowAuthor(Id) {
            showCalimaticLoader();
            $(".error_msg").html('');
            var data = new FormData($('#add_form')[0]);

            $.ajax({
                headers: {
                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                },
                method: "POST",
                url: "{{ url('unfollow-author') }}",
                data: {
                    id: Id
                },
                success: function(data, textStatus, jqXHR) {
                    if (data.status == 1) {

                        $('#followingAuthor' + Id).text('Follow');
                    }

                }
            }).done(function() {
                $("#success_msg").html("Data Save Successfully");
                //  window.location.href = "{{ url('admin/users') }}";
                // location.reload();
            }).fail(function(data, textStatus, jqXHR) {
                var json_data = JSON.parse(data.responseText);
                $.each(json_data.errors, function(key, value) {
                    $("#" + key).after("<span class='error_msg' style='color: red;font-weigh: 600'>" +
                        value + "</span>");
                });
            });
            HideCalimaticLoader();
        }
    </script>
